const PokeDetail = () => {
  const { name } = useParams();
  const [pokemon, setPokemon] = useState(null);
  const [loading, setLoading] = useState(true);  // Nuevo estado para controlar la carga

  useEffect(() => {
    const fetchPokemonDetails = async () => {
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${name}`);
        const data = await response.json();
        setPokemon(data);
      } catch (error) {
        console.error("Error al obtener detalles del Pokémon:", error);
      } finally {
        setLoading(false);  // Marcar la carga como completa, independientemente del resultado
      }
    };

    fetchPokemonDetails();
  }, [name]);

  return (
    <div
      className="container mt-4"
      style={{
        position: 'relative',
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {/* Contenedor para la imagen de fondo con opacidad */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          opacity: 0.5,
          zIndex: 0,
        }}
      />

      {/* Estilo para el título */}
      <h1
        className="mb-4 text-dark"
        style={{
          fontWeight: "bold",
          zIndex: 1,
        }}
      >
        Details Pokemon
      </h1>
      <br />

      {loading ? (  // Mostrar mensaje de carga mientras se obtienen los datos
        <p className="text-muted">Cargando detalles del Pokémon...</p>
      ) : (
        <div className="card" style={{ position: "relative" }}>
          <div
            className="card-body"
            style={{
              transition: "background-color 0.3s",
              backgroundColor: "#fff", // Color de fondo predeterminado
            }}
            onMouseEnter={() => {
              document.querySelector(".card-body").style.backgroundColor = "#e0e0e0";
            }}
            onMouseLeave={() => {
              document.querySelector(".card-body").style.backgroundColor = "#fff";
            }}
          >
            {pokemon && (
              <>
                <h2 className="card-title">{pokemon.name}</h2>
                {pokemon.sprites && (
                  <img
                    src={pokemon.sprites.front_default}
                    alt={pokemon.name}
                    className="img-fluid mb-3"
                  />
                )}
                {/* Agregar más detalles según sea necesario */}
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default PokeDetail;
