import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const PokeDetail = () => {
  const { name } = useParams();
  const [pokemon, setPokemon] = useState(null);

  useEffect(() => {
    const fetchPokemonDetails = async () => {
      try {
        // Realiza una llamada a la API para obtener los detalles del Pokemon
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${name}`);
        const data = await response.json();

        // Actualiza el estado 'pokemon' con los detalles obtenidos
        setPokemon(data);
      } catch (error) {
        console.error("Error al obtener detalles del Pokémon:", error);
        // Puedes manejar errores aquí, por ejemplo, mostrar un mensaje de error.
      }
    };

    // Llama a la función para obtener los detalles del Pokemon
    fetchPokemonDetails();
  }, [name]);

  // Define la URL de la imagen de fondo
  const backgroundImageUrl = "URL_DE_TU_IMAGEN_DE_FONDO";

  return (
    <div
      className="container mt-4"
      style={{
        backgroundImage: `url(${backgroundImageUrl})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh", // Ajusta la altura según sea necesario
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <h1 className="mb-4 text-light">Detalles del Pokémon</h1>

      {pokemon ? (
        <div className="card">
          <div className="card-body">
            <h2 className="card-title">{pokemon.name}</h2>
            {pokemon.sprites && (
              <img
                src={pokemon.sprites.front_default}
                alt={pokemon.name}
                className="img-fluid mb-3"
              />
            )}
            {/* Agrega más detalles según lo necesario */}
          </div>
        </div>
      ) : (
        <p className="text-muted">Cargando detalles del Pokémon...</p>
      )}
    </div>
  );
};

export default PokeDetail;
