// export default PokeDetail;
import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import backgroundImage from "../assets/img/b.png"; // Importa la imagen de fondo

const PokeDetail = () => {
  const { name } = useParams();
  const [pokemon, setPokemon] = useState(null);

  useEffect(() => {
    const fetchPokemonDetails = async () => {
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${name}`);
        const data = await response.json();
        setPokemon(data);
      } catch (error) {
        console.error("Error al obtener detalles del Pokémon:", error);
      }
    };

    fetchPokemonDetails();
  }, [name]);

  if (!pokemon) {
    return (
      <div className="container mt-4" style={{ minHeight: "100vh", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }}>
        <div style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', backgroundImage: `url(${backgroundImage})`, backgroundSize: "cover", backgroundPosition: "center", opacity: 0.5, zIndex: 0 }} />
        <h1 className="mb-4 text-dark" style={{ fontWeight: "bold", zIndex: 1 }}>Details Pokemon</h1>
        <div className="text-center mt-4">
          <p className="text-muted mb-3">Cargando detalles del Pokémon...</p>
          <Link to="/" className="btn btn-primary">
            Ir a la Página Principal
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="container mt-4" style={{ position: 'relative', minHeight: "100vh", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }}>
      <div style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%', backgroundImage: `url(${backgroundImage})`, backgroundSize: "cover", backgroundPosition: "center", opacity: 0.5, zIndex: 0 }} />
      <h1 className="mb-4 text-dark" style={{ fontWeight: "bold", zIndex: 1 }}>Details Pokemon</h1>
      <br />
      <div className="card" style={{ position: "relative" }}>
        <div
          className="card-body"
          style={{
            transition: "background-color 0.3s",
            backgroundColor: "#fff",
          }}
          onMouseEnter={() => {
            document.querySelector(".card-body").style.backgroundColor = "#e0e0e0";
          }}
          onMouseLeave={() => {
            document.querySelector(".card-body").style.backgroundColor = "#fff";
          }}
        >
          <h2 className="card-title">{pokemon.name}</h2>
          {pokemon.sprites && (
            <img
              src={pokemon.sprites.front_default}
              alt={pokemon.name}
              className="img-fluid mb-3"
            />
          )}
          {/* Agrega más detalles según lo necesario */}
        </div>
      </div>
    </div>
  );
};

export default PokeDetail;
