import 'bootstrap/dist/css/bootstrap.min.css';
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import backgroundImage from "../assets/img/b.png"; // Importa la imagen de fondo

const PokeDetail = () => {
  const { name } = useParams();
  const [pokemon, setPokemon] = useState(null);

  useEffect(() => {
    const fetchPokemonDetails = async () => {
      try {
        const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${name}`);
        const data = await response.json();
        setPokemon(data);
      } catch (error) {
        console.error("Error al obtener detalles del Pokémon:", error);
      }
    };

    fetchPokemonDetails();
  }, [name]);

  return (
    <div
      className="container mt-4"
      style={{
        position: 'relative',
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {/* Contenedor para la imagen de fondo con opacidad */}
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          opacity: 0.5,
          zIndex: 0,
        }}
      />

      {/* Estilo para el título */}
      <h1
        className="mb-4 text-dark"
        style={{
          fontWeight: "bold",
          zIndex: 1,
        }}
      >
        Details Pokemon
      </h1>
      <br />

      {pokemon ? (
        <div className="card" style={{ width: "18rem", position: "relative" }}>
          <img
            src={pokemon.sprites.front_default}
            alt={pokemon.name}
            className="card-img-top"
            style={{ borderRadius: "8px 8px 0 0" }}
          />
          <div className="card-body">
            <h5 className="card-title">{pokemon.name}</h5>
            {/* Agrega más detalles según lo necesario */}
          </div>
        </div>
      ) : (
        <p className="text-muted">Cargando detalles del Pokémon...</p>
      )}
    </div>
  );
};

export default PokeDetail;
